import React from "react";
import "./ProjectCard.css";
import { Fade } from "react-reveal";
import { style } from "glamor";

export default function ProjectCard({ repo, theme }) {
  function openRepoinNewTab(url) {
    var win = window.open(url, "_blank");
    win.focus();
  }

  const styles = style({
    color: "white",
    backgroundColor: "white",
    boxShadow: "rgba(0, 0, 0, 0.2) 0px 20px 60px -30px",
    padding: "2rem",
    borderRadius: "5px",
    height: "100%",
    transition: "all 0.2s ease-in-out",
    ":hover": {
      boxShadow: `${theme.color} 0 2px 15px`,
    },
  });
   if(repo.id=="0"){
  return (
    <div>
      <Fade bottom duration={2000} distance="40px">
        <div
          {...styles}
          key={repo.id}
          style={{ backgroundColor: theme.ProjectCard }}
        >
          <div className="repo-name-div">
            <p className="repo-name" style={{color: theme.text}}>
              {repo.name}
            </p>
          </div>
          {repo.description.map((sentence) => {
          return (
          <p className="repo-description" style={{ color: theme.text }}>
            {sentence}</p>);})};
          <p className="repo-description" style={{color:theme.text}}>
              <b><u><h3>Technologies used:</h3></u></b>
          </p>
          {repo.language.map((sentence) => {
        return (<i className="a" style={{ color: theme.text }}>
          {sentence}</i>);})};
         <a href={repo.url} className="repo-description1">code</a>
        </div>
      </Fade>
    </div>
  );
}
}
